const initialState = {
    counter:0,

}
function counterReducer (state = initialState, action) {
    if (action.type === 'handleCountIncrement'){
        return  {
            ...state,
            counter : state.value+1
        }
    }
    else if (action.type === 'handleCountDecrement'){
        return {
            ...state,
            counter : state.value-1
        }
    }
    else {
        return {...state}
    }
    }

export default counterReducer
