import { useState } from "react";
const Counter = () => {
    const [counter, setCounter] = useState(0);

    const handleCountIncrement = () => {
        counter = counter+1;
    }
    const handleCountDecrement = () => {
        counter = counter -1;
    }
    return ( 
        <div>
        <h1>Counter Page</h1>
        <div className="counter">
        
            <h2 className="count">{counter}</h2>
                
                {/* passing from hook to onclick directement  */}
                <div>
                <button className="mybutton" onClick={()=> {setCounter={handleCountIncrement};
                    console.log(counter)}
                    }> Increment
                </button>
                <button className="mybutton" onClick={()=> {setCounter={handleCountDecrement};
                    console.log(counter)}
                    }> Decrement 
                </button>
                </div>
            </div>
        </div>
     );
}
 
export default Counter;
