import { useState , useEffect} from "react";
import InfoList from "./InfoList";
import useFetch from "./useFetch";

const Home = () => {


    const {data : infos , isLoading , Errors } = useFetch('http://localhost:8000/infos')
 
 
     const handleDelete = (id) =>{
         const newinfo = infos.filter(info =>info.id !== id);
         setData(newinfo);
     }

    
    return ( 
        <div className="home">
            <h2>Home page</h2>
            {Errors && <h2> {Errors} </h2>}
            {isLoading && <h2>loading data .... </h2>}
            {infos && <InfoList infos={infos} infoTitle = 'All Information :'/>}
            {infos && <InfoList infos={infos.filter((info) => info.title === 'Mouhamed')} infoTitle = 'Only Mouhamed Info:' handleDelete= {handleDelete}/>}
            {infos && <InfoList infos={infos.filter((info) => info.id === 3)} infoTitle = 'Only Nawel Info:' handleDelete= {handleDelete}/>}
            {infos &&<InfoList infos={infos.filter((info) => info.work === 'Manager')} infoTitle = 'Only Managers Info:' handleDelete= {handleDelete}/>}

        </div>
     );
}
 
export default Home;
