import { useState , useEffect} from "react";
import DetailsInfo from "./DetailsList";
import InfoList from "./InfoList";

const Home = () => {
    
    const [firstName, setFirstName] = useState('nawel');
    const [clickSimple , setClickSimple] = useState ('i am a handle click !! click to see what happen :D');
    const [clickWithParam , setClickWithParam] = useState ('nothing to say just click below to see what happen')
    const [age , setAge] = useState (27);
    const [counter, setCounter] = useState(0);
    const [completed , setCompleted] = useState(false);
    const [infos, setInfos] = useState(null);

    const [details, setdetails] = useState([
        {id :1 , completeName : 'Elleuch'},
        {id :2 , completeName : 'Affes'},
        {id :3 , completeName :'Felah'}
    ]); 


    const handleClick = (e) => {
        console.log('i am a handle click',e);
        setClickSimple('i am a simple click after clicking btn');
     }
   
     const handleClickWithParam = (name , e) =>{
         console.log ('hello ' + name, e.target);
         let greeting = ('hello ' + name + ' so cool no ??' );
         setClickWithParam(greeting);
     }
 
     const handleClickHook = () =>{
         setFirstName('azer');
         setAge(23);
         setCompleted (true);
         console.log (completed)
         }
 
     const handleDelete = (id) =>{
         const newinfo = infos.filter(info =>info.id !== id);
         setInfos(newinfo);
     }

     useEffect( ()=> {
         fetch('http://localhost:8000/infos').then(res => {
             return res.json();
         }).then(
             data => {
                 setInfos(data);
             });
          }, [infos]);
    return ( 
        <div className="home">
            <h2>this a home page</h2>
            <div className="info">
                <h3>{clickSimple}</h3>
                <button className ="mybutton" onClick={handleClick}>simple button with function without param</button>
            </div>
            <div className="info">
                <h3>{clickWithParam}</h3>
                <button className="mybutton" onClick={(e)=> handleClickWithParam('nawel',e)}> button with click function(params)</button>
            </div>
            <div className="info">
                <h3>My name is {firstName} i am {age} years old my mission is completed {completed} </h3>
                <button className="mybutton"onClick={handleClickHook}>change name and age using hook</button>
            </div>
            <div className="info">
                <h2>{counter}</h2>
                {/* passing from hook to onclick directement  */}
                <button className="mybutton" onClick={()=> {setCounter(counter +1);
                    console.log(counter)}
                    }> Counter
                </button>
            </div>

           {props.infos && <InfoList infos={infos} infoTitle = 'All Information :' handleDelete= {handleDelete}/>}
           <InfoList infos={infos.filter((info) => info.title === 'Mouhamed')} infoTitle = 'Only Mouhamed Info:' handleDelete= {handleDelete}/>
           <InfoList infos={infos.filter((info) => info.id === 3)} infoTitle = 'Only Nawel Info:' handleDelete= {handleDelete}/>
           <InfoList infos={infos.filter((info) => info.work === 'Manager')} infoTitle = 'Only Managers Info:' handleDelete= {handleDelete}/>

           <DetailsInfo details= {details} detailsTitle = 'All details' />
                 

        </div>
     );
}
 
export default Home;
