import { decrement, increment } from "./redux/actions/counterActions";
import { connect } from "react-redux";
const Counter = () => {
    return ( 

        <div>
        <h1>Counter Page</h1>
        <div className="counter">
        
            <h2 className="count">{counter}</h2>
                
                {/* passing from hook to onclick directement  */}
                <div>
                <button className="mybutton" onClick= {onIncrement}> Increment
                </button>
                <button className="mybutton" onClick = {onDecrement}> Decrement 
                </button>
                </div>
            </div>
        </div>
     );
    }

const mapDispatchToProps = dispatch => {
    return {
      onIncrement: () => dispatch(Action.increment()),
      onDecrement: () => dispatch(Action.decrement()),
      onReset: () => dispatch(Action.reset())
    };
  };
  
  const mapStateToProps = state => {
    return { count: state };
  };
  
  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Counter);
