const initialState = {
    counter: 0,

}
function counterReducer(state = initialState, action) {
    switch (action.type) {
        case 'INCREMENT_COUNTER':
            return {
                ...state,
                counter: state.counter + 1
            }
        case 'INCREMENT_COUNTER':
            return {
                ...state,
                counter: state.counter - 1
            }
        default:
            return state
    }

}

export default counterReducer
