import { useState } from "react";
const Counter = () => {
    const [counter, setCounter] = useState(0);
    return ( 
        <div>
        <h1>Counter Page</h1>
        <div className="info">
                <h2>{counter}</h2>
                {/* passing from hook to onclick directement  */}
                <div>
                <button className="mybutton" onClick={()=> {setCounter(counter +1);
                    console.log(counter)}
                    }> Increment
                </button>
                <button className="mybutton" onClick={()=> {setCounter(counter -1);
                    console.log(counter)}
                    }> Decrement 
                </button>
                </div>
            </div>
        </div>
     );
}
 
export default Counter;
