import { increment , decrement } from "../actions/counterActions";
const initialState = {
    counter: 0,

}
function counterReducer (state = initialState, action) {
    switch (action.type) {
        case increment:
            return state + 1
        case decrement:
            return state - 1
        default:
            return state
        }
    
}

export default counterReducer
