            {/*infos && <InfoList infos={infos.filter((info) => info.title === 'Mouhamed')} infoTitle = 'Only Mouhamed Info:' handleDelete= {handleDelete}/>}
            {infos && <InfoList infos={infos.filter((info) => info.id === 3)} infoTitle = 'Only Nawel Info:' handleDelete= {handleDelete}/>}
