import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
const Counter = () => {
    const counter = useSelector((state) => state.counter)
    const dispatch = useDispatch();
    const onChangeCounter = (key) => {
        dispatch({ type: key })
    }
   
    return ( 
        <div>
        <h1>Counter Page</h1>
        <div className="counter">
        
            <h2 className="count">{counter}</h2>
                
                {/* passing from hook to onclick directement  */}
                <div>
                <button className="mybutton" onClick={() => onChangeCounter('increment')}> Increment</button>
                <button className="mybutton" onClick ={() => onChangeCounter('decrement')}> Decrement </button>
                </div>
            </div>
        </div>
     );
}

export default Counter;
