import { Link } from "react-router-dom";

const InfoList = (props) => {

    /*const handleDelete = (id) =>{
        const newinfo = props.infos.filter(info =>info.id !== id);
        setdata(newinfo);
    }*/
    return ( 
        <div>
            <h2 className="content">{props.infoTitle}</h2>
             {
                    props.infos.map( (info) => (
                    <div className="info" key={info.id}>
                        <Link to={'/infos/${info.id}'}>
                        <h2>Mr/Mrs : {info.title}</h2>
                        <h3>Work : {info.work}</h3>
                        <button className="delete-button" onClick={() => props.handleDelete(info.id)}>Delete</button> 
                        </Link>
                    </div>
                  
             ))}
            
        </div>
     );
}
 
export default InfoList;
