const InfoList = (props) => {
 
    const handleDelete = (id) =>{
        const newinfo = infos.filter(info =>info.id !== id);
        setData(newinfo);
    }
    return ( 
        <div>
            <h2 className="content">{props.infoTitle}</h2>
             {
                    props.infos.map( (info) => (
                    <div className="info" key={info.id}>
                        <h2>Mr/Mrs : {info.title}</h2>
                        <h3>Work : {info.work}</h3>
                        <button className="delete-button" onClick={() => handleDelete(info.id)}>Delete</button> 
                    </div>
                  
             ))}
            
        </div>
     );
}
 
export default InfoList;
