import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { increment , decrement } from "./redux/actions/counterActions";
const Counter = () => {
    const counter = useSelector(() => counter);
    const dispatch = useDispatch();

    return ( 
        <div>
        <h1>Counter Page</h1>
        <div className="counter">
        
            <h2 className="count">{counter}</h2>
                
                {/* passing from hook to onclick directement  */}
                <div>
                <button className="mybutton" onClick={() => dispatch(increment())}> Increment</button>
                <button className="mybutton" onClick ={() => dispatch(decrement())}> Decrement </button>
                </div>
            </div>
        </div>
     );
}

export default Counter;
