import { useState , useEffect} from "react";
import DetailsInfo from "./DetailsList";
import InfoList from "./InfoList";
import useFetch from "./useFetch";

const Home = () => {
    

    const [counter, setCounter] = useState(0);
   
    

    const [details, setdetails] = useState([
        {id :1 , completeName : 'Elleuch'},
        {id :2 , completeName : 'Affes'},
        {id :3 , completeName :'Felah'}
    ]); 

    const {data : infos , isLoading , Errors } = useFetch('http://localhost:8000/infos')
 
 
     /*const handleDelete = (id) =>{
         const newinfo = infos.filter(info =>info.id !== id);
         setData(newinfo);
     }*/

    
    return ( 
        <div className="home">
            <h2>this a home page</h2>
            <div className="info">
                <h2>{counter}</h2>
                {/* passing from hook to onclick directement  */}
                <button className="mybutton" onClick={()=> {setCounter(counter +1);
                    console.log(counter)}
                    }> Counter
                </button>
            </div>
            {Errors && <h2> {Errors} </h2>}
            {isLoading && <h2>loading data .... </h2>}
           {infos && <InfoList infos={infos} infoTitle = 'All Information :'/>}
          
           <DetailsInfo details= {details} detailsTitle = 'All details' />
                 

        </div>
     );
}
 
export default Home;
